package com.example.api_retrofit_app.presentation.screens.viewmodelsimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.example.api_retrofit_app.data.Repositoryimport com.example.api_retrofit_app.models.ItemModelListimport kotlinx.coroutines.launchimport retrofit2.Responseclass ListViewModel() : ViewModel() {    private val repository = Repository()     val liveDataList = MutableLiveData<Response<ItemModelList>>()    fun getList() {       viewModelScope.launch {           liveDataList.value = repository.getListPhotos()       }    }}